import React, { Component } from 'react'

export default class CreateEmployeeComponent extends Component {
    constructor(props){
        super(props)

        this.state ={
            firstName: '', lastName: '', emailId: ''
        }
    }

    changeFirstNameHandler = (event) => {
        this.setState({firstName: event.target.value});
    }

    changeLastNameHandler = (event) => {
        this.setState({lastName: event.target.value});
    }

    changeEmailHandler = (event) => {
        this.setState({emailId: event.target.value})
    }
  render() {
    return (
      <div>
        <div className="container">
            <div className="row">
                <div className="card col-md-6 offset-md-3 offset-md-3">
                    <h3 className='text-center'>Add Employees</h3>
                    <div className='card-body'>
                        <form>
                            <div className ="form-group">
                                <label>First Name:</label>
                                <input placeholder='First Name' name='firstName' className='form-center'
                                value={this.state.firstName} onChange={this.changeFirstNameHandler}/>
                            </div>
                            <div className ="form-group">
                                <label>Last Name:</label>
                                <input placeholder='Last Name' name='lastName' className='form-center'
                                value={this.state.lastName} onChange={this.changeLastNameHandler}/>
                            </div>
                            <div className ="form-group">
                                <label>Emaid Id:</label>
                                <input placeholder='Email Id' name='emailId' className='form-center'
                                value={this.state.emailId} onChange={this.changeEmailNameHandler}/>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
      </div>
    )
  }
}
