import React, { Component } from 'react'
import EmployeeService from '../service/EmployeeService';

class UpdateEmployeeComponent extends Component {
    constructor(props){
        super(props)

        this.state ={
            id: this.props.mathc.params.id,
            firstName: '',
             lastName: '',
              emailId: ''
        }

        this.changeFirstNameHandler = this.changeFirstNameHandler.bind(this);
        this.changeLastNameHandler = this.changeLastNameHandler.bind(this);
        this.changeEmailHandler = this.changeEmailHandler.bind(this);
        this.updateEmployee = this.updateEmployee.bind(this);
    }

    componentDidMount(){
        EmployeeService.getEmployeeByID(this.state.id).then((res) => {
            let employee = res.data;
            this.setState({firstName: employee.firstName,
            lastName: employee.lastName,
        emailId: employee.emailId});
        });
    }

    changeFirstNameHandler = (event) => {
        this.setState({firstName: event.target.value});
    }

    changeLastNameHandler = (event) => {
        this.setState({lastName: event.target.value});
    }

    changeEmailHandler = (event) => {
        this.setState({emailId: event.target.value});
    }

    updateEmployee = (e) => {
        e.preventDefault();
        let employee ={firstName: this.state.firstName,lastName: this.state.lastName, emailId:this.state.emailId}

        EmployeeService.createEmployee(employee).then(res => {
            this.props.history.push('/employees');
        });
    }

    cancel(){
        this.props.history.push('/employees');
    }

  render() {
    return (
      <div>
        <div className="container">
            <div className="row">
                <div className="card col-md-6 offset-md-3 offset-md-3">
                    <h3 className='text-center'>Update Employee</h3>
                    <div className='card-body'>
                        <form>
                            <div className ="form-group">
                                <label>First Name:</label>
                                <input placeholder='First Name' name='firstName' className='form-center'
                                value={this.state.firstName} onChange={this.changeFirstNameHandler}/>
                            </div>
                            <div className ="form-group">
                                <label>Last Name:</label>
                                <input placeholder='Last Name' name='lastName' className='form-center'
                                value={this.state.lastName} onChange={this.changeLastNameHandler}/>
                            </div>
                            <div className ="form-group">
                                <label>Email Id:</label>
                                <input placeholder='Email Address' name='emailId' className='form-center'
                                value={this.state.emailId} onChange={this.changeEmailHandler}/>
                            </div>
                            <button className='btn btn-success' onClick={this.updateEmployee}>Save</button>
                            <button className='btn btn-danger' onClick={this.cancel.bind(this)} style={{marginLeft: "10px"}}>Cancel</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
      </div>
    )
  }
}

export default UpdateEmployeeComponent
